def TwoSum(arr, target):
	j = len(arr)-1
	i = 0
	while j <= i:
		if arr[i]+arr[j] > target:
			j = j-1
		elif arr[i] + arr[j] < target:
			i = i+1
		else:
			break
	return arr[i], arr[j]
		
		
		

def Triplet(arr):
	triple_list = []
	arr = sorted(arr)
	global_store = dict()
	for i in range(len(arr)):
		arr_sort = arr.copy()
		val = arr_sort.pop(i)
		#print(len(arr_sort))
		
		target = -val
		j = len(arr_sort)-1
		i = 0
		while 1:
			print(1)
			if j <= i:
				break
			if arr_sort[i]+arr_sort[j] > target:
				j = j-1
			elif arr_sort[i] + arr_sort[j] < target:
				i = i+1
			else:
				a,b,c = sorted((arr_sort[i], arr_sort[j], val))
				string_ = str(a)+str(b)+str(c)
				if string_ not in global_store:
					global_store[string_] = [a,b,c]
				i = i+1
				j = j-1
			

	list_ = list(global_store.values())
	return list_
	
